rads = [1, 2, 3, 7, 12, 16]
   
# -----------------------------------------------------------------------
def rotz(p, a):
    a = (pi *  a/180)
    x,y,z = p[:3]
    _x = x * cos(a) - y * sin(a)
    _y = x * sin(a) + y * cos(a)
    _z = z
    return (_x,_y,_z) + p[3:]
    
def rotx(p, a):
    a = (pi *  a/180)
    x,y,z = p[:3]
    _z = z * cos(a) - y * sin(a)
    _y = z * sin(a) + y * cos(a)
    _x = x
    return (_x,_y,_z) + p[3:]

def roty(p, a):
    a = (pi *  a/180)
    x,y,z = p[:3]
    _z = z * cos(a) - x * sin(a)
    _x = z * sin(a) + x * cos(a)
    _y = y
    return (_x,_y,_z) + p[3:]

def drw_stars(bd, fn):
    img = Image.new("RGB", (IMG_W, IMG_H), BKG)
    drw = ImageDraw.Draw(img, mode="RGB")

    for x,y,z,d in bd:
        x += xC
        y += yC
        
        c = clrs[d >> 4]
        r = rads[d & 0x0F]        
        
        if r == 1:
            drw.point((x, y), fill=c)
        elif (d & 0x0F) == 5:
            drw.pieslice((x-r,y-r,x+r,y+r), 30, 360-30, fill=c)
            drw.pieslice((x-2,y-10,x+2,y-6), 0, 360, fill=BKG)
        else:
            drw.pieslice((x-r,y-r,x+r,y+r), 0, 360, fill=c)

    img.save(fn)

